import axios from 'axios';

// URL de votre endpoint RunPod
const API_URL = 'https://api.runpod.ai/v2/votre-id-endpoint/run';

// Clé API RunPod (à stocker en sécurité dans un .env en production)
const API_KEY = 'votre-clé-api-runpod';

const api = axios.create({
  baseURL: API_URL,
  headers: {
    'Content-Type': 'application/json',
    'Authorization': `Bearer ${API_KEY}`
  }
});

export const callExcelMCP = async (command, params, files = []) => {
  // Convertir les fichiers en base64 si nécessaire
  const fileData = await Promise.all(
    files.map(async (file) => {
      if (file instanceof File) {
        const content = await fileToBase64(file);
        return {
          name: file.name,
          content: content
        };
      }
      return file; // Déjà formaté
    })
  );

  try {
    const response = await api.post('', {
      input: {
        command: command,
        params: params,
        files: fileData
      }
    });

    // RunPod renvoie généralement les données dans un format spécifique
    if (response.data && response.data.output) {
      return response.data.output;
    }

    return response.data;
  } catch (error) {
    console.error('API error:', error);
    throw error;
  }
};

// Fonction pour convertir un fichier en base64
const fileToBase64 = (file) => {
  return new Promise((resolve, reject) => {
    const reader = new FileReader();
    reader.readAsDataURL(file);
    reader.onload = () => {
      // Enlever le préfixe "data:application/...;base64,"
      const base64String = reader.result.split(',')[1];
      resolve(base64String);
    };
    reader.onerror = (error) => reject(error);
  });
};

export default {
  callExcelMCP
};
