import axios from 'axios';

// Remplacer cette URL par l'URL de votre RunPod dédié
const LOCAL_API_URL = 'http://localhost:8001';

// API pour l'agent local
const localApi = axios.create({
  baseURL: LOCAL_API_URL,
  headers: {
    'Content-Type': 'application/json'
  }
});

/**
 * Vérifie le statut du serveur local et la disponibilité d'Excel
 * @returns {Promise} - Statut du serveur et Excel
 */
export const checkMcpStatus = async () => {
  try {
    const response = await localApi.get('/mcp/status');
    return response.data;
  } catch (error) {
    console.error('Status check error:', error);
    return { 
      success: false, 
      error: error.message || 'Erreur de connexion au serveur local'
    };
  }
};

/**
 * Récupère la liste des classeurs Excel ouverts
 * @returns {Promise} - Liste des classeurs
 */
export const getOpenWorkbooks = async () => {
  try {
    const response = await localApi.get('/mcp/workbooks');
    return response.data;
  } catch (error) {
    console.error('Workbooks fetch error:', error);
    throw error;
  }
};

/**
 * Lit les données d'une feuille dans un classeur ouvert
 * @param {string} workbook - Nom du classeur
 * @param {string} sheet - Nom de la feuille
 * @returns {Promise} - Données de la feuille
 */
export const readSheetData = async (workbook, sheet) => {
  try {
    const response = await localApi.post('/mcp/read_sheet', {
      workbook: workbook,
      sheet: sheet
    });
    return response.data;
  } catch (error) {
    console.error('Sheet read error:', error);
    throw error;
  }
};

/**
 * Analyse une feuille Excel avec embedding
 * @param {string} workbook - Nom du classeur
 * @param {string} sheet - Nom de la feuille
 * @param {string} query - Requête utilisateur
 * @param {number} topK - Nombre de résultats à retourner
 * @returns {Promise} - Résultat de l'analyse
 */
export const analyzeSheetWithEmbedding = async (workbook, sheet, query, topK = 5) => {
  try {
    const response = await localApi.post('/mcp/analyze', {
      workbook: workbook,
      sheet: sheet,
      query: query,
      top_k: topK
    });
    return response.data;
  } catch (error) {
    console.error('Embedding analysis error:', error);
    throw error;
  }
};

export default {
  checkMcpStatus,
  getOpenWorkbooks,
  readSheetData,
  analyzeSheetWithEmbedding
};
